          "content": [
            {
              "type": "paragraph",
              "attrs": {
                "textAlign": "left"
              },
              "content": [
                {
                  "type": "text",
                  "marks": [
                    {
                      "type": "bold"
                    }
                  ],
                  "text": "Scalable"
                },
                {
                  "type": "text",
                  "text": " − Python provides a better structure and support for large programs than shell scripting."
                }
              ]
            }
          ]
        }
